svg.script:
        // apparently Firefox fires the SVGLoad event before this script runs so
        // I have to put it in the attribute above.
        // but we can make an onclick for reloading!
        
        document.onclick = run;
        setInterval(run, 30000);
        
        function run() {
            var PI = 3.14159265359;
            var sin = Math.sin; 
            var cos = Math.cos;
            var rnd = Math.random;
            
            function randint(lo, hi) { return Math.floor(lo + rnd() * (hi - lo)) }            

            function Coz() {
                var i;
                var n = randint(2, 6);
                var fs = [], dfs = [], f = 1, df = 0;
                for(var i=0; i<n; i++) { 
                    fs.push([rnd(), f]);
                    dfs.push([rnd(), df]);
                    f += randint(1, 3);
                    df += randint(1, 3);                    
                }
                fs.sort();
                dfs.sort();

                var ps = [], i;
                for (i = 0; i < n; i++) {
                    ps.push({               
                        a: PI * (rnd() - 0.5),
                        f: fs[i][1],
                        p: 2 * PI * (rnd() - 0.5),
                        da: PI * (rnd() - 0.5),
                        df: dfs[i][1],
                        dp: 2 * PI * (rnd() - 0.5),
                    });
                }
                i = 0; st = ps[i].a+'  |  '+ps[i].f+'  |  '+ps[i].d+'  |  '+ps[i].df + '\n';
                //i = 1; st += ps[i].a+'  |  '+ps[i].f+'  |  '+ps[i].d+'  |  '+ps[i].df + '\n';
                // alert(st);
                return function (phi) {
                    var r = 0, j;
                    for (var j = 0; j < n; j++) {
                        r += ps[j].a * sin(ps[j].p + phi * ps[j].f + ps[j].da * sin(ps[j].dp + phi * ps[j].df)) 
                    }
                    return r;
                };
            }
            
            // draw path
            var t, x, y, i;
            var fx = Coz();
            var fy = Coz();
            var mx = 1, my = 1;
            var d = [];
            
            // check bounds
            for (i = 0; i < 100; i++) {
                t = (2 * PI * i / 100);
                x = Math.abs(fx(t));
                y = Math.abs(fy(t));
                if (x > mx) mx = x;
                if (y > my) my = y;                
            }
            
            for (i = 0; i < 1000; i++) {
                t = (2 * PI * i / 1000);
                x = fx(t);
                y = fy(t);                
                
                x *= 300 / mx;
                y *= 220 / my;
                
                d.push((i > 0 ? 'L' : 'M') + ' ' + x + ' ' + y);
            }
            var pad = document.getElementById('pad');
            pad.setAttribute('d', d.join(' ') + ' Z');            
        };
